package test;

public class EvenNumberCheck {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int num = 23;
		
		System.out.println(num%2);
		
		if(num%2 == 0) {
			System.out.println("The number is Even");
		}else {
			System.out.println("The number is odd");
		
		}
	}

}
